import {dirname, join} from 'path';

import {readFile} from 'fs-extra';

import * as Utility from './utility';


const AMD_CJS = ['require', 'exports', 'module'];

/**
 * Key for module path & value for local variable
 *
 * @typedef {Object} DependencyMap
 */


/**
 * CommonJS or AMD module
 */
export default  class Module {
    /**
     * @param {string}  name       - Path of this module
     * @param {string}  path       - Root path of the package which this module belongs to
     * @param {boolean} includeAll - Treat NPM modules as CommonJS modules
     */
    constructor(name, path, includeAll) {
        /**
         * Path of this module
         *
         * @type {string}
         */
        this.name = name;

        /**
         * Directory path of this module
         *
         * @type {string}
         */
        this.base = dirname( name ).replace(/\\/g, '/');

        /**
         * Root path of the package which this module belongs to
         *
         * @type {string}
         */
        this.path = path;

        /**
         * @type     {Object}
         * @property {DependencyMap} compile - Compile-time dependency from AMD
         * @property {DependencyMap} runtime - Runtime dependency from CommonJS
         * @property {DependencyMap} outside - Outside dependency from NPM
         */
        this.dependency = {
            compile:  { },
            runtime:  { },
            outside:  includeAll ? null : { }
        };
    }

    /**
     * @protected
     *
     * @param {string} name - Name of a module
     *
     * @return {string}
     */
    static identifierOf(name) {  return  name.replace(/[./]+/g, '_');  }

    /**
     * Identifier of this module in JS code
     *
     * @type {string}
     */
    get identifier() {  return  Module.identifierOf( this.name );  }

    /**
     * Paths of all the dependency needed to be bundled
     *
     * @type {string[]}
     */
    get dependencyPath() {

        return  Object.keys( this.dependency.compile ).concat(
            Object.keys( this.dependency.runtime )
        );
    }

    /**
     * @protected
     *
     * @return {?string} Entry file path of this module in `./node_modules/`
     */
    searchNPM() {

        for (let method  of  ['getNPMFile', 'getNPMPackage', 'getNPMIndex']) {

            let path;

            if (path = Utility[method]( this.name ))  return path;
        }
    }

    /**
     * @protected
     *
     * @return {string} Original source code of this module
     */
    async load() {

        this.source = await readFile(
            ((! this.dependency.outside)  &&  /^\w/.test( this.name ))  ?
                this.searchNPM()  :  join(this.path, `${this.name}.js`)
        );

        return  this.source = (this.source + '').replace(/\r\n/g, '\n');
    }

    /**
     * Add a depended module of this module
     *
     * @protected
     *
     * @param {string} type      - `compile` for AMD & `runtime` for CJS
     * @param {string} name      - Name of a module
     * @param {string} [varName] - Variable name of a module in another module
     */
    addChild(type, name, varName) {

        if ((type === 'compile')  &&  AMD_CJS.includes( name ))  return;

        const NPM = /^\w/.test( name );

        if (this.dependency.outside && NPM)  type = 'outside';

        name = (NPM ? '' : './')  +  join(this.base, name).replace(/\\/g, '/');

        this.dependency[ type ][ name ] = varName  ||  Module.identifierOf( name );
    }

    /**
     * @protected
     *
     * @return {DependencyMap} Compile-time dependency
     */
    parseAMD() {

        this.source = this.source.replace(
            /define\((?:\s*\[([\s\S]*?)\]\s*,)?\s*function\s*\(([\s\S]*?)\)\s*\{([\s\S]+)\}\s*\);?/,
            (_, modName, varName, body) => {

                var index = 0;  varName = varName.trim().split( /\s*,\s*/ );

                modName.replace(
                    /(?:'|")(.+?)(?:'|")/g,
                    (_, name)  =>
                        this.addChild('compile',  name,  varName[ index ])  ||  index++
                );

                return  body.replace(/^\n([\s\S]+)\n$/, '$1');
            }
        );

        return this.dependency.compile;
    }

    /**
     * @protected
     *
     * @return {DependencyMap} Runtime dependency
     */
    parseCJS() {

        this.source.replace(
            /(?:(?:var|let|const)\s+[\s\S]+?=\s*)?require\(\s*(?:'|")(.+?)(?:'|")\s*\)/mg,
            (match, modName)  =>  this.addChild('runtime', modName)  ||  match
        );

        return this.dependency.runtime;
    }

    /**
     * @return {string} Factory code of this parsed module
     */
    async parse() {

        await this.load();

        this.parseAMD();

        this.parseCJS();

        return  this.source = `function ${this.identifier}(${

            Object.values( this.dependency.compile ).concat( AMD_CJS ).join(', ')
        }) {${
            this.source
        }}`;
    }

    /**
     * @return {string} Factory code of this parsed module
     */
    toString() {

        return  ('source' in this)  ?  (this.source + '')  :  '';
    }
}

import {basename, dirname} from 'path';

import Module from './Module';

import {merge} from './utility';

const Array_proto = Array.prototype;


/**
 * Package to be bundled
 */
export default  class Package {
    /**
     * @param {string}  path         - The entry file path of this package
     *                                 (relative to `process.cwd()`)
     * @param {boolean} [includeAll] - Include NPM modules in the final bundle
     * @param {boolean} [noLog]      - Disable log output
     */
    constructor(path, includeAll, noLog) {
        /**
         * Module name of this bundled package
         *
         * @type {string}
         */
        this.name = basename( path );

        /**
         * The root path of this package (relative to `process.cwd()`)
         *
         * @type {string}
         */
        this.base = dirname( path );

        /**
         * Whether include NPM modules in the final bundle
         *
         * @type {boolean}
         */
        this.includeAll = includeAll;

        /**
         * Module count of this package
         *
         * @type {number}
         */
        this.length = 0;

        /**
         * Reference count of modules in this package
         * (key for name & value for count)
         *
         * @type {Object}
         */
        this.module = { };

        /**
         * Whether show logs during the bundle process
         *
         * @type {boolean}
         */
        this.showLog = (! noLog);
    }

    /**
     * @param {Module|string} module - Instance or name of a module
     *
     * @return {number}
     */
    indexOf(module) {

        for (let i = 0;  this[i];  i++)
            if (
                (module instanceof Module)  ?
                    (module === this[i])  :  (module === this[i].name)
            )
                return i;

        return -1;
    }

    /**
     * @protected
     *
     * @param {string} modName - Path of a module
     *
     * @return {?Module} New module
     */
    register(modName) {

        const index = this.indexOf( modName );

        this.module[modName] = (this.module[modName] || 0)  +  1;

        if (index < 0) {

            Array_proto.unshift.call(
                this,  new Module(modName, this.base, this.includeAll)
            );

            return this[0];
        }

        Array_proto.sort.call(this,  (A, B) => {

            if (B.dependencyPath.includes( A.name ))  return -1;

            if (A.dependencyPath.includes( B.name ))  return 1;

            return  this.module[ B.name ]  -  this.module[ A.name ];
        });
    }

    /**
     * Entry module of this package
     *
     * @type {Module}
     */
    get entry() {  return  this[this.length - 1];  }

    /**
     * @protected
     *
     * @param {string} modName - Path of a module
     */
    async parse(modName) {

        const module = this.register( modName );

        if (! module)  return;

        await module.parse();

        if ( this.showLog )
            console.info(`âˆš Module "${modName}" has been bundled`);

        await Promise.all(
            module.dependencyPath.map(path  =>  this.parse( path ))
        );
    }

    /**
     * Outside dependencies of this package
     *
     * @type {DependencyMap}
     */
    get outDependency() {

        return  this.includeAll  ?  [ ]  :  Object.assign(
            ... Array.from(this,  module => module.dependency.outside)
        );
    }

    /**
     * @protected
     *
     * @param {function(modName: string[], varName: string[]): string} bundler
     *
     * @return {string} Bundled source with UMD wrapper
     */
    wrap(bundler) {

        const outside = this.outDependency;

        const modName = Object.keys( outside ), varName = Object.values( outside );

        return `
//
//  Generated by https://www.npmjs.com/package/amd-bundle
//
(function (factory) {

    if ((typeof define === 'function')  &&  define.amd)
        define('${this.name}', ${JSON.stringify( modName )}, factory);
    else if (typeof module === 'object')
        return  module.exports = factory(${modName.map(name => `require('${name}')`)});
    else
        return  this.${this.name} = factory(${modName.map(name => `this.${name}`)});

})(${bundler(modName, varName)});`.trim();
    }

    /**
     * @return {string} Source code of this package
     */
    async bundle() {

        await this.parse(`./${this.name}`);

        return  this.wrap((modName, varName) => `function (${varName}) {

    var module = {
        ${Array.from(
        this,  item => `'${item.name}':  {exports: { }}`
    ).concat(
        modName.map((name, index)  =>  `'${name}':  {exports: ${varName[index]}}`)
    ).join(',\n        ')}
    };

${merge}

    function _require_(base, path) {

        return module[
            /^\\w/.test( path )  ?  path  :  ('./' + merge(base, path))
        ].exports;
    }

    var require = _require_.bind(null, './');

${Array.from(this,  item => item.source).join('\n\n')}

    ${Array.from(this,  item => `module['${item.name}'].exports = ${item.identifier}(${

        Object.keys( item.dependency.compile ).map(
            child => `require('${child}')`
        ).concat(
            `_require_.bind(null, '${item.base}')`,
            `require('${item.name}')`,
            `module['${item.name}']`
        ).join(', ')

    }) || require('${item.name}');`).join('\n\n    ')}

    return require('${this.entry.name}');
}`);
    }
}

import {join} from 'path';

import {existsSync, readFileSync, statSync} from 'fs';


/**
 * This will be used in the bundled source
 *
 * @param {string} base - Root path
 * @param {string} path - Path relative to `base`
 *
 * @return {string} Joined & normalized path
 */
export function merge(base, path) {

    return  (base + '/' + path)
        .replace(/\/\//g, '/')
        .replace(/[^/.]+\/\.\.\//g, '')
        .replace(/\.\//g,  function (match, index, input) {

            return  (input[index - 1]  ===  '.')  ?  match  :  '';
        });
}


function inNPM(path) {

    if (existsSync(path = join('node_modules', path)))
        return  path.replace(/\\/g, '/');
}


function findNPMFile(list) {

    for (let path of list)  if (path = inNPM( path ))  return path;
}


/**
 * @param {string} name - Name of a module
 *
 * @return {?string} Valid `name` in `./node_modules/` is a JS or JSON file
 */
export function getNPMFile(name) {

    return  findNPMFile([name, `${name}.js`, `${name}.json`]);
}


/**
 * @param {string} name - Name of a module
 *
 * @return {?string} Valid `name` in `./node_modules/` is `index.js` or `index.json`
 */
export function getNPMIndex(name) {

    return  findNPMFile([`${name}/index.js`, `${name}/index.json`]);
}


/**
 * @param {string} name - Name of a module
 *
 * @return {?string} Entry file path of an existing `package.json`
 */
export function getNPMPackage(name) {

    var path = inNPM(`${name}/package.json`);

    if (! path)  return;

    const config = JSON.parse( readFileSync( path ) );

    for (let key  of  ['main', 'browser'])
        if (path = inNPM( join(name, config[key]) ))
            return  statSync( path ).isFile()  ?  path  :  getNPMIndex( path );
}

