    var C = require('./c');

    return  {a: A, c: C, test: require('test')};
});

import {execSync} from 'child_process';

import {removeSync} from 'fs-extra';


import {merge, getNPMFile, getNPMIndex, getNPMPackage} from '../libs/utility';

import Module from '../libs/Module';

import Package from '../libs/Package';

var bundle_code;


describe('Utility',  () => {
    /**
     * @test {merge}
     */
    it('Merge module paths',  () => {

        merge('./../test//../', './example').should.be.equal('../example');
    });

    /**
     * @test {getNPMFile}
     */
    it('Get path of JS modules',  () => {

        getNPMFile('test').should.be.equal('node_modules/test.js');
    });

    /**
     * @test {getNPMIndex}
     */
    it('Get "index.js" path of a module',  () => {

        getNPMIndex('koapache/source').should.be.equal(
            'node_modules/koapache/source/index.js'
        );
    });

    /**
     * @test {getNPMPackage}
     */
    it('Get entry file path from "package.json" of a module',  () => {

        getNPMPackage('koapache').should.be.equal(
            'node_modules/koapache/source/index.js'
        );
    });
});


/**
 * @test {Module}
 */
describe('Module parser',  () => {

    var module;

    before(()  =>  (module = new Module('index', './test/example/')).load());

    /**
     * @test {Module#parseAMD}
     */
    it('Parse AMD',  () => {

        module.parseAMD().should.be.eql( {'./a': 'A'} );

        (module + '').should.be.equal(`/* AMD module */

    var C = require('./c');

    return  {a: A, c: C, test: require('test')};
`);
    });

    /**
     * @test {Module#parseCJS}
     */
    it('Parse CommonJS',  () => {

        module.parseCJS().should.be.eql( {'./c': '_c'} );
    });

    /**
     * @test {Module#parse}
     */
    it('Parse all',  () => module.parse().should.be.fulfilledWith(`

function index(A, require, exports, module) {/* AMD module */

    var C = require('./c');

    return  {a: A, c: C, test: require('test')};
}`.trim())
    );

    /**
     * @test {Module#dependencyPath}
     */
    it('Get paths of the dependency',  () => {

        module.dependencyPath.should.be.eql( ['./a', './c'] );
    });
});


/**
 * @test {Package}
 */
describe('Package bundler',  () => {

    var pack = new Package('');

    /**
     * @test {Package#register}
     */
    it('Register module',  () => {

        pack.register('a');

        pack.register('b');

        pack.register('c');

        pack.register('b');

        Array.from(pack,  module => module.name).should.be.eql(['b', 'c', 'a']);
    });

    /**
     * @test {Package#parse}
     */
    it('Parse package',  async () => {

        pack = new Package('./test/example/index');

        await pack.parse('./index');

        Array.from(pack,  module => module.name).should.be.eql([
            './libs/b',  './c',  './a',  './index'
        ]);
    });

    /**
     * @test {Package#wrap}
     */
    it('UMD wrapper',  () => {

        pack.wrap(
            (modName, varName)  =>
                `function (${varName}) {  ${JSON.stringify( modName )};  }`
        ).should.be.equal(`
//
//  Generated by https://www.npmjs.com/package/amd-bundle
//
(function (factory) {

    if ((typeof define === 'function')  &&  define.amd)
        define('index', ["test"], factory);
    else if (typeof module === 'object')
        return  module.exports = factory(require('test'));
    else
        return  this.index = factory(this.test);

})(function (test) {  ["test"];  });`.trim());
    });

    async function testBundle(all) {

        bundle_code = await (new Package('./test/example/index', all)).bundle();

        try {
            eval( bundle_code ).should.be.eql({
                a:     'This is A',
                c:     'This is C',
                test:  {test: 1}
            });
        } catch (error) {

            console.warn( bundle_code );  throw error;
        }
    }

    /**
     * @test {Package#bundle}
     */
    it('Bundle all dependencies',  testBundle.bind(null, true));

    /**
     * @test {Package#bundle}
     */
    it('Bundle without NPM packages',  testBundle.bind(null, false));
});


describe('Command line',  () => {

    it('Output to a file',  () => {

        (execSync('node index test/example/index test/example/build') + '')
            .should.be.startWith(`
âˆš Module "./index" has been bundled
âˆš Module "./a" has been bundled
âˆš Module "./c" has been bundled
âˆš Module "./libs/b" has been bundled`.trim()
            );
    });


    it('Write into stdout without printing',  () => {

        (execSync('node index test/example/index test/example/build -s') + '')
            .should.be.eql( bundle_code );
    });

    after(() => removeSync('test/example/build'));
});
