import {dirname, join} from 'path';

import {readFile} from 'fs';

import {promisify} from 'util';

const loadFile = promisify( readFile ), AMD_CJS = ['require', 'exports', 'module'];


/**
 * Key for module path & value for local variable
 *
 * @typedef {Object} DependencyMap
 */


/**
 * CommonJS or AMD module
 */
export default  class Module {
    /**
     * @param {string} name - Path of this module
     * @param {string} path - Root path of the package which this module belongs to
     */
    constructor(name, path) {
        /**
         * Path of this module
         *
         * @type {string}
         */
        this.name = name;

        /**
         * Directory path of this module
         *
         * @type {string}
         */
        this.base = dirname( name ).replace(/\\/g, '/');

        /**
         * Root path of the package which this module belongs to
         *
         * @type {string}
         */
        this.path = path;

        /**
         * @type     {Object}
         * @property {DependencyMap} compile - Compile-time dependency
         * @property {DependencyMap} runtime - Runtime dependency
         */
        this.dependency = {
            compile:  { },
            runtime:  { }
        };

        /**
         * Modules which this module depends
         *
         * @type {Module[]}
         */
        this.children = [ ];
    }

    /**
     * Identifier of this module in JS code
     *
     * @type {string}
     */
    get identifier() {

        return  this.name.replace(/[./]+/g, '_').replace(/^_/, '');
    }

    /**
     * Paths of all the dependency
     *
     * @type {string[]}
     */
    get dependencyPath() {

        return  Object.keys( this.dependency.compile ).concat(
            Object.keys( this.dependency.runtime )
        );
    }

    /**
     * @protected
     *
     * @return {string} Original source code of this module
     */
    async load() {

        return  this.source = (await loadFile(
            join(this.path, `${this.name}.js`),  {encoding: 'utf-8'}
        )).replace(/\r\n/g, '\n');
    }

    addChild(type, name, varName) {

        this.dependency[ type ][ join(this.base, name).replace(/\\/g, '/') ] = varName;
    }

    /**
     * @protected
     *
     * @return {DependencyMap} Compile-time dependency
     */
    parseAMD() {

        this.source = this.source.replace(
            /define\((?:\s*\[([\s\S]*?)\]\s*,)?\s*function\s*\(([\s\S]*?)\)\s*\{([\s\S]+)\}\s*\);?/,
            (_, modName, varName, body) => {

                var index = 0;  varName = varName.trim().split( /\s*,\s*/ );

                modName.replace(/(?:'|")(.+?)(?:'|")/g,  (_, name) => {

                    if (! AMD_CJS.includes( name ))
                        this.addChild('compile',  name,  varName[ index ]);

                    index++;
                });

                return  body.replace(/^\n([\s\S]+)\n$/, '$1');
            }
        );

        return this.dependency.compile;
    }

    /**
     * @protected
     *
     * @return {DependencyMap} Runtime dependency
     */
    parseCJS() {

        this.source.replace(
            /(?:(?:var|let|const)\s+(.+?)\s*=\s*)?require\(\s*(?:'|")(.+?)(?:'|")\s*\)/mg,
            (match, varName, modName)  =>
                this.addChild('runtime', modName, varName)  ||  match
        );

        return this.dependency.runtime;
    }

    /**
     * @return {string} Factory code of this parsed module
     */
    async parse() {

        await this.load();

        this.parseAMD();

        this.parseCJS();

        return  this.source = `function ${this.identifier}(${

            Object.values( this.dependency.compile ).concat( AMD_CJS ).join(', ')
        }) {${
            this.source
        }}`;
    }

    /**
     * @return {string} Factory code of this parsed module
     */
    toString() {

        return  ('source' in this)  ?  (this.source + '')  :  '';
    }
}

import {basename, dirname} from 'path';

import Module from './Module';


/**
 * This will be used in the bundled source
 *
 * @param {string} base - Root path
 * @param {string} path - Path relative to `base`
 *
 * @return {string} Joined & normalized path
 */
export function join(base, path) {

    return  (base + '/' + path)
        .replace(/\/\//g, '/')
        .replace(/[^/\.]+\/\.\.\//g, '')
        .replace(/\.\//g,  function (match, index, input) {

            return  (input[index - 1]  ===  '.')  ?  match  :  '';
        });
}


/**
 * Package to be bundled
 */
export default  class Package {
    /**
     * @param {string} path - The entry file path of this package
     *                        (relative to `process.cwd()`)
     */
    constructor(path) {
        /**
         * The entry file path of this package (relative to `process.cwd()`)
         *
         * @type {string}
         */
        this.path = path;

        /**
         * The root path of this package (relative to `process.cwd()`)
         *
         * @type {string}
         */
        this.base = dirname( path );

        /**
         * Module count of this package
         *
         * @type {number}
         */
        this.length = 0;

        /**
         * Module index in this package (key for name & value for index)
         *
         * @type {Object}
         */
        this.module = { };
    }

    /**
     * @protected
     *
     * @param {string} modName - Path of a module
     *
     * @return {Module} New or loaded module
     */
    register(modName) {

        const index = this.module[ modName ];  var module;

        if (index != null)
            module = Array.prototype.splice.call(this, -index, 1)[0];
        else
            module = new Module(modName, this.base);

        Array.prototype.unshift.call(this, module);

        this.module[ modName ] = this.length;

        return module;
    }

    /**
     * Entry module of this package
     *
     * @type {Module}
     */
    get entry() {  return  this[this.length - 1];  }

    /**
     * @protected
     *
     * @param {Module} module
     * @param {Module} parent
     *
     * @return {boolean} Whether `module` has a circular dependency
     */
    hasCircular(module, parent) {

        parent.children.push( module );

        try {  JSON.stringify( this.entry );  } catch (error) {

            if ( error.message.includes('circular') )
                return  (!! parent.children.pop());
        }
    }

    /**
     * @protected
     *
     * @param {string}  modName - Path of a module
     * @param {?Module} parent  - Module depends `modName`
     */
    async parse(modName, parent) {

        const module = this.register( modName );

        if (parent  &&  this.hasCircular(module, parent))
            return  console.warn(`Module "${modName}" has a circular reference`);

        await module.parse();

        await Promise.all(
            module.dependencyPath.map(path  =>  this.parse(path, module))
        );
    }

    /**
     * @return {string} Factory code of this package
     */
    async bundle() {  /* eslint no-useless-escape: "off" */

        await this.parse( basename( this.path ) );

        return `function () {

    var module = {
        ${Array.from(
        this,  item => `'${item.name}':  {exports: { }}`
    ).join(',\n        ')}
    };

${join}

    function _require_(base, path) {

        return  module[join(base, path)].exports;
    }

    var require = _require_.bind(null, './');

${Array.from(this,  item => item.source).join('\n\n')}

    ${Array.from(this,  item => `module['${item.name}'].exports = ${item.identifier}(${

        Object.keys( item.dependency.compile ).map(
            child => `require('${child}')`
        ).concat(
            `_require_.bind(null, '${item.base}')`,
            `require('${item.name}')`,
            `module['${item.name}']`
        ).join(', ')

    }) || require('${item.name}');`).join('\n\n    ')}

    return require('${this.entry.name}');
}`;
    }
}

